"use strict";var jumbotronRightArrow=document.querySelector(".jumbotron-right-arrow"),jumbotronLeftArrow=document.querySelector(".jumbotron-left-arrow"),jumbotronFigures=document.querySelectorAll(".jumbotron_figure"),moveJumbotronRight=function(){return jumbotronFigures.forEach(function(t){if(t.classList.contains("viewport-offset-0")||t.classList.contains("viewport-offset-1")){var o=t.classList[1].slice(-1);return t.classList.remove("viewport-offset-".concat(o)),t.classList.add("viewport-offset-".concat(Number(o)+1))}if(t.classList.contains("viewport-offset-2"))return t.classList.remove("viewport-offset-2"),t.classList.add("viewport-offset-0")})},moveJumbotronLeft=function(){return jumbotronFigures.forEach(function(t){if(t.classList.contains("viewport-offset-0"))return t.classList.remove("viewport-offset-0"),t.classList.add("viewport-offset-2");if(t.classList.contains("viewport-offset-1")||t.classList.contains("viewport-offset-2")){var o=t.classList[1].slice(-1);return t.classList.remove("viewport-offset-".concat(o)),t.classList.add("viewport-offset-".concat(Number(o)-1))}})};jumbotronRightArrow.addEventListener("click",moveJumbotronRight),jumbotronLeftArrow.addEventListener("click",moveJumbotronLeft);